import './ExpenseList.cs'
import ExpenseItem from './ExpenseItem'

const ExpenseList = (props) => {

    if(props.items.length === 0){
        return <h2 className='expenses-list__fallback'>No expenses </h2>
    }
    return(
        <ul className='expenses-list'>
            {props.items.map((expense) => (
            <ExpenseItem
              key={expense.id}
              title={expense.title}
              amount={expense.amount}
              date={expense.date}
              location={expense.location}
            />
            ))
            }
        {/* {props.items.length === 0 ? <p className='expenses-list__fallback'>No expense is present..</p> : props.items.map((expense) => (
            <ExpenseItem
              key={expense.id}
              title={expense.title}
              amount={expense.amount}
              date={expense.date}
              location={expense.location}
            />
          ))
        }
        

        {props.items.length === 1 ? <p className='expenses-list__fallback'>Only one expense is there, add more </p> : props.items.map((expense) => (
            <ExpenseItem
              key={expense.id}
              title={expense.title}
              amount={expense.amount}
              date={expense.date}
              location={expense.location}
            />
          ))} */}
        </ul>  
    );
}
export default ExpenseList
