import React, { useState } from 'react';

import ExpenseItem from './ExpenseItem';
import ExpensesFilter from './ExpenseFilter';
import Card from '../UI/Card';
import './Expense.css';

const Expenses = (props) => {
  const [filteredYear, setFilteredYear] = useState('2020');

  const filterChangeHandler = (selectedYear) => {
    setFilteredYear(selectedYear);
  };

  const filterExpense = props.items.filter(expense => {
    return expense.date.getFullYear().toString() === filteredYear;
  })

  // const itemFilter = <p>Add Expense</p>;
  // if(filterExpense.length === 0){
  //   itemFilter = <p>No Expense is there..</p>;
  // }else if(filterExpense.length === 1){
  //   itemFilter = <p>Only One Expense is present, please try to add more expense.. </p>;
  // }else if(filterExpense.length > 1){
  //   itemFilter = filterExpense.map((expense) => (
  //     <ExpenseItem
  //       key={expense.id}
  //       title={expense.title}
  //       amount={expense.amount}
  //       date={expense.date}
  //       location={expense.location}
  //     />
  //   ))
  // }

  return (
    <div>
      <Card className='expenses'>
        <ExpensesFilter
          selected={filteredYear}
          onChangeFilter={filterChangeHandler}
        />
        {filterExpense.length === 0 ? <p>No Expense is present..</p> : filterExpense.map((expense) => (
      <ExpenseItem
        key={expense.id}
        title={expense.title}
        amount={expense.amount}
        date={expense.date}
        location={expense.location}
      />
    ))}
      </Card>
    </div>
  );
};

export default Expenses;
